" > src/models/Storyteller.ts && echo "import * as fs from 'fs';
import { Student } from './models/Student';

export class FileService {
    static saveToFile(filename: string, data: string): void {
        fs.writeFileSync(filename, data, 'utf-8');
    }

    static readFromFile(filename: string): string {
        return fs.existsSync(filename) ? fs.readFileSync(filename, 'utf-8') : '';
    }

    static saveStudents(students: Student[], filename: string): void {
        const content = students.map(s =>
            `Student ${s.firstName}${s.lastName}
{ "firstname": "${s.firstName}", "lastname": "${s.lastName}", "studentId": "${s.studentId}", "course": "${s.course}", "gender": "${s.gender}", "grade": "${s.grade}", "id": "${s.id}" };
`
        ).join('
');
        this.saveToFile(filename, content);
    }

    static loadStudents(filename: string): Student[] {
        const content = this.readFromFile(filename);
        if (!content) return [];

        const lines = content.split('
').filter(l => l.includes('{'));
        return lines.map(line => {
            const json = line.substring(line.indexOf('{'), line.lastIndexOf('}') + 1);
            const obj = JSON.parse(json);
            return new Student(obj.firstname, obj.lastname, obj.id, obj.studentId, Number(obj.course), obj.gender, Number(obj.grade));
        });
    }
}
